import React from 'react';

function FilterList(props) {


  return(
    //Empty div to be populated by props passed down
  <div id="filter-bar">
    <div id="filter-title">Filter:</div>
    <select id="filter-options"
            value= {null}
            onChange={
              (e) => {
                let selection = document.getElementById('filter-options');
                let selectionValue = selection.getAttribute("value")
                selectionValue = (e.target.key - 100);
                props.onFilterMarker(null, selectionValue);
              }
            }>
      <option value="See All Locations"
              className="filter-option"
              >See All Locations</option>
      {props.locations.map((location, index) => (
      <option key={index + 100}
              value={location}
              id="index"
              className="filter-option"
            //   onSelect={
            //     (e) => {
            //     props.onFilterMarker(e.target.value, e.target.id);
            // }}
          >{location.title}</option>
      ))}
    </select>
    {/* <div id="list-title">Filter Selections</div>
    <ul id="drop-list">
      {props.locations.map((location, index) => (
        <li key={index + 100}
              id="index"
              className="list-selection"
              onClick={() => props.onFilterMarker(location, index)}
              >{location.title}</li>
      ))}
    </ul>
    <div className="show-all-button"
          onClick={() => props.showAllLocations()}>
      See All
    </div> */}
  </div>
  )
}


export default FilterList
